import React from "react";
import { updateNewText, createNewPost } from "../../../redux/actions/actions";
import MyPosts from "./MyPosts";

const MyPostsContainer = (props) => {
    let state = props.store.getState();

    let addPost = () => {
        props.dispatch(createNewPost());
    };

    let onChangeText = () => {
        props.dispatch(updateNewText());
    };

    return <MyPosts posts={state.newPostText} profilePage={state.profilePage} addPost={addPost} onChangeText={onChangeText} />;
};

export default MyPostsContainer;
