import { renderReactTree } from "../render";

const state = {
    profilePage: {
        dialogs: [
            { id: 1, name: "Leo" },
            { id: 2, name: "Mark" },
            { id: 3, name: "Alf" },
            { id: 4, name: "Bean" },
            { id: 5, name: "Bill" },
        ],
        newPostText: "New text",
    },
    dialogsPage: {
        messages: [
            { id: 1, message: "Yo" },
            { id: 2, message: "Hi" },
            { id: 3, message: "No way" },
            { id: 4, message: "No doubt" },
            { id: 5, message: "Wait for me!" },
        ],


    },
};

window.state = state;

export const addPosts = (newMessage) => {
    let newPost = {
        id: 3,
        message: newMessage,
        like: 0,
    };
    state.dialogsPage.posts.push(newPost);
    renderReactTree(state);
};

export const updateNewText = (newText) => {
    state.profilePage.newPostText = newText;
    renderReactTree(state);
};

export default state;
