const state = {
    profilePage: {
        dialogs: [
            { id: 1, name: "Leo" },
            { id: 2, name: "Mark" },
            { id: 3, name: "Alf" },
            { id: 4, name: "Bean" },
            { id: 5, name: "Bill" },
        ]
    },
    dialogsPage: {
        messages: [
            { id: 1, message: "Yo" },
            { id: 2, message: "Hi" },
            { id: 3, message: "No way" },
            { id: 4, message: "No doubt" },
            { id: 5, message: "Wait for me!" },
        ],

        posts: [
            { id: 1, message: "hello", like: 12 },
            { id: 2, message: "I's my first post!", like: 5 },
        ]
    },
};

export const addPosts = (newMessage) => {
    let newPost = {
        id: 6,
        message: newMessage,
        like
    }
}

export default state;
