import React from "react";
import { connect } from "react-redux";
import { updateNewText, createNewPost } from "../../../redux/actions/actions";
import MyPosts from "./MyPosts";

// const MyPostsContainer = (props) => {
//     let state = props.store.getState();

//     let addPost = () => {
//         props.store.dispatch(createNewPost());
//     };

//     let onChangeText = (text) => {
//         let action = updateNewText(text);
//         props.store.dispatch(action);
//     };

//     return <MyPosts posts={state.profilePage.posts} addPost={addPost} updateNewText={onChangeText} />;
// };

const mapStateToProps = (state) => {
    let { posts, newPostText } = state.profilePage;
    return {
        posts: posts,
        newPostText
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        createNewPost: () => {
            dispatch(createNewPost());
        },
        updateNewText: (text) => {
            dispatch(updateNewText(text));
        },
    };
};

const MyPostsContainer = connect(mapStateToProps, mapDispatchToProps)(MyPosts);

export default MyPostsContainer;
