let renderReactTree = () => {
    console.log('state changed');
};

const store = {
    state: {
        profilePage: {
            posts: [
                { id: 1, message: "hello", like: 12 },
                { id: 2, message: "I's my first post!", like: 5 },
            ],
            newPostText: "New text",
        },

        dialogsPage: {
            dialogs: [
                { id: 1, name: "Leo" },
                { id: 2, name: "Mark" },
                { id: 3, name: "Alf" },
                { id: 4, name: "Bean" },
                { id: 5, name: "Bill" },
            ],
            messages: [
                { id: 1, message: "Yo" },
                { id: 2, message: "Hi" },
                { id: 3, message: "No way" },
                { id: 4, message: "No doubt" },
                { id: 5, message: "Wait for me!" },
            ],
            newMessageText: "Add message",
        },

    },

    addPosts() {
        let newPost = {
            id: 3,
            message: this.state.profilePage.newPostText,
            like: 0,
        };
        this.state.profilePage.posts.push(newPost);
        this.state.profilePage.newPostText = '';
        renderReactTree(state);
    },

    updateNewText(newText) {
        this.state.profilePage.newPostText = newText;
        renderReactTree(state);
    },

    addMessage() {
        let message = {
            message: state.dialogsPage.newMessageText,
        };
        state.dialogsPage.messages.push(message);
        state.dialogsPage.newMessageText = '';
        renderReactTree(state);
    },

    updateNewMessage(newMessage) {
        state.dialogsPage.newMessageText = newMessage;
        renderReactTree(state);
    },

};

export const subscribe = (observer) => {
    renderReactTree = observer;
};

export default store;
