import React from "react";
import { connect } from "react-redux";
import { createNewMessage, updateNewMessage } from "../../redux/actions/actions";
import Dialogs from "./Dialogs";

const DialogsContainer = (props) => {
    let state = props.store.getState();

    let newMessage = () => {
        props.store.dispatch(createNewMessage());
    };

    let onChangeMessage = (text) => {
        const action = updateNewMessage(text);
        props.store.dispatch(action);
    };

    return (
        <Dialogs
            dialogs={state.dialogsPage.dialogs}
            messages={state.dialogsPage.messages}
            newMessageText={state.dialogsPage.newMessageText}
            newMessage={newMessage}
            updateNewMessage={onChangeMessage}
        />
    );

    const mapStateToProps = (state) => {
        return {
            profilePage: state.profilePage,
            dialogsPage: state.dialogsPage,
        };
    };

};

export default connect(ma)(DialogsContainer);
