import React from "react";
import styles from "../Dialogs/Dialogs.module.css";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";

const Dialogs = (props) => {
    let dialogsElements = props.state.profilePage.dialogs.map((dialog) => <DialogItem name={dialog.name} id={dialog.id} />);
    let messagesElements = props.state.dialogsPage.messages.map((message) => <Message message={message.message} />);

let valueTextarea = React.createRef();

let newMessage = () => {
    const addMessage = valueTextarea.current.value;
    alert(addMessage);
};

    return (
        <div className={styles.dialogs}>
            <div>{dialogsElements}</div>
            <div className={styles.messages}>{messagesElements}</div>
            <div>
                <textarea ref={valueTextarea} className={styles.dialogText} name="" id="" cols="30" rows="10"></textarea>
                <button onClick={newMessage} className={styles.dialogBtn}>Push</button>
            </div>
        </div>
    );
};

export default Dialogs;
