import React from "react";
import styles from "./MyPosts.module.css";
import Post from "./Post/Post";

const MyPosts = (props) => {
    let postElement = props.state.dialogsPage.posts.map((data) => <Post message={data.message} like={data.like} />);

    let newPostElem = React.createRef();

    let addPost = () => {
        let myText = newPostElem.current.value;
        props.addPosts(myText);
        pro
    };

    let onChangeText = () => {
        let newText = newPostElem.current.value;
        props.updateNewText(newText);
        // props.updateNewText('');

    };

    return (
        <div className={styles.posts}>
            <h3>My posts</h3>
            <div className={styles.formBlock}>
                <div>
                    {" "}
                    <textarea className={styles.textArea} ref={newPostElem} onChange={onChangeText} value={props.state.profilePage.newPostText} />
                </div>
                <div>
                    {" "}
                    <button onClick={addPost} className={styles.btn}>
                        Add post
                    </button>
                </div>
            </div>
            <div className={styles.block}>{postElement}</div>
        </div>
    );
};

export default MyPosts;
