import React from "react";
import styles from "../Dialogs/Dialogs.module.css";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import { addNewMessage, updateNewMessage } from "../../redux/store";

const Dialogs = (props) => {
    console.log(props);
    let dialogsElements = props.state.dialogsPage.dialogs.map((dialog) => (
        <DialogItem name={dialog.name} id={dialog.id} />
    ));
    let messagesElements = props.state.dialogsPage.messages.map((message) => <Message message={message.message} />);

    let valueTextarea = React.createRef();

    let newMessage = () => {
        props.dispatch(createNewMessage());
    };

    let onChangeMessage = () => {
        const text = valueTextarea.current.value;
        props.dispatch(updateNewMessage(text));
    };

    return (
        <div className={styles.dialogs}>
            <div>{dialogsElements}</div>
            <div className={styles.messages}>{messagesElements}</div>
            <div>
                <textarea
                    onChange={onChangeMessage}
                    ref={valueTextarea}
                    className={styles.dialogText}
                    rows="10"
                    value={props.state.dialogsPage.newMessageText}
                ></textarea>
                <button onClick={newMessage} className={styles.dialogBtn}>
                    Push
                </button>
            </div>
        </div>
    );
};

export default Dialogs;
